size(250); import olympiad; import geometry; defaultpen(linewidth(0.8));

draw((-10,0)..(-5,0.8)..(0,1)..(5,0.8)..(10,0)^^(10,0)..(5,-0.8)..(0,-1)..(-5,-0.8)..(-10,0));

