draw((2*pi/7,0)--(5/14*pi,pi/2)--(3*pi/7,0),blue);
draw((0,0)--(pi/2,0));
draw((0,0)--(0,pi/2));

draw((1.8,1.2)--(2.2,1.2),red);
draw((1.8,1.0)--(2.2,1.0),green);
draw((1.8,0.8)--(2.2,0.8),blue);

