label("$\frac{\pi}{2}$", (pi/2,0), S);
label("$\frac{\pi}{7}$", (pi/7,0), S);
label("$\frac{2 \pi}{7}$", (2*pi/7,0), S);
label("$\frac{3 \pi}{7}$", (3*pi/7,0), S);

