label("$D$", (31,0), SE);
label("$B$", (6,8), NW);
label("$C$", (16,8), NE);
label("$E$", (6,0), S);
label("$F$", (16,0), S);
label("10", (3,5), W);
label("8", (6,4), E, red);
label("8", (16,4), E, red);
