triple[] Pp = {-P[0], -P[1], -P[2], -P[3], -P[4]};

// draw octahedron
triple pt(int k){ return (3*P[k] + P[1])/4; }
triple ptp(int k){ return (3*Pp[k] + Pp[1])/4; }
