draw(C--E,dashed);
draw(A--M);
draw(M--E,dashed);
draw(B--N);
draw(N--E,dashed);
draw(D--Q);
draw(Q--E,dashed);
draw(Ap--Bp--Cp--Dp--cycle);
draw(Ap--M);
draw(M--Ep,dashed);
draw(Bp--N);
draw(N--Ep,dashed);
draw(Cp--Ep,dashed);
draw(Dp--Q);
draw(Q--Ep,dashed);
draw(Q--M--N);
draw(Q--P--N,dashed);

